package com.example.lee.firstRound;

import java.util.HashMap;
import java.util.Map;

/**
 * Created by benbendaisy on 1/2/15.
 * Given a string S and a string T, count the number of distinct subsequences of T in S.
 * A subsequence of a string is a new string which is formed from the original string by deleting some (can be none) of the characters without disturbing the relative positions of the remaining characters. (ie, "ACE" is a subsequence of "ABCDE" while "AEC" is not).
 * Here is an example:
 * S = "rabbbit", T = "rabbit"
 * Return 3.
 */
public class DistinctSubsequences {
    public int numDistinctI(String S, String T) {
        if(S == null || T == null){
            return 0;
        }
        int sLen = S.length();
        int tLen = T.length();
        //dp represents the numbers that substring of S from beginning to i matchs the substring of T from begining to j
        int[][] dp = new int[sLen + 1][tLen + 1];

        //when S is empty, dp is 0
        for(int j = 1; j <= tLen; j++){
            dp[0][j] = 0;
        }

        //when T is empty, dp is 1 as empty string is substring of any string
        for(int i = 0; i <= sLen; i++){
            dp[i][0] = 1;
        }

        for(int i = 1; i <= sLen; i++){
            for(int j = 1; j <= tLen; j++){
                //the number of mathing should equal the number of mathing of previous substring of S
                dp[i][j] = dp[i - 1][j];

                //if previous character of both S and T equals, then the total number should add previous one
                if(S.charAt(i - 1) == T.charAt(j - 1)){
                    dp[i][j] += dp[i - 1][j - 1];
                }
            }
        }

        return dp[sLen][tLen];
    }


    //recursive with Memoization
    public int numDistinct(String S, String T) {
        if(S == null || S.isEmpty() || T == null || T.isEmpty()){
            return 0;
        }
        Map<String, Integer> map = new HashMap<String, Integer>();
        return numDistinctHelper(S, T, map);
    }

    private int numDistinctHelper(String S, String T, Map<String, Integer> map){
        if((S.isEmpty() && T.isEmpty()) || T.isEmpty()){
            return 1;
        } else if(S.isEmpty()){
            return 0;
        } else if(S.equals(T)){
            return 1;
        }

        String str = S + T;
        if(map.containsKey(str)){
            return map.get(str);
        }

        int count = 0;
        if(S.charAt(0) == T.charAt(0)){
            count += numDistinctHelper(S.substring(1), T.substring(1), map);
        }

        count += numDistinctHelper(S.substring(1), T, map);

        map.put(str, count);

        return count;
    }

    //another recursive way without memorization
    public int numDistinctII(String S, String T) {
        if(S == null || T == null){
            return 0;
        }
        if (S.length() == 0) {
            return T.length() == 0 ? 1 : 0;
        } else if (T.length() == 0) {
            return 1;
        } else if(S.length() < T.length()){
            return 0;
        }
        int count = 0;
        for (int i = 0; i < S.length(); i++) {
            if (S.charAt(i) == T.charAt(0)) {
                count += numDistinct(S.substring(i + 1), T.substring(1));
            }
        }
        return count;
    }

    public static void main(String[] args) {
        DistinctSubsequences distinctSubsequences = new DistinctSubsequences();
        System.out.println(distinctSubsequences.numDistinct("", "rwmimatmhydhbujebqehjprarwfkoebcxxqfktayaaeheys"));
    }
}
