package com.example.lee.secondRound;

/**
 * Created by benbendaisy on 5/24/15.
 *
 * Given a string S, you are allowed to convert it to a palindrome by adding characters in front of it. Find and return the shortest palindrome you can find by performing this transformation.
 *
 * For example:
 *
 * Given "aacecaaa", return "aaacecaaa".
 *
 * Given "abcd", return "dcbabcd".
 */
public class ShortestPalindrome {
    public String shortestPalindrome(String s) {
        if (null == s || s.length() < 2) {
            return s;
        }
        int len = s.length(), idx = len - 1;
        while (idx > 1 && !isPalindrome(s, idx)) idx--;

        String res = s;
        if (idx < len) {
            res = reverseString(s.substring(idx)) + res;
        }
        return res;
    }

    private boolean isPalindrome(String s, int idx) {
        int l = 0, r = idx;
        while (l < r) {
            if (s.charAt(l) != s.charAt(r)) return false;
            l++;
            r--;
        }
        return true;
    }

    private String reverseString(String str) {
        char[] arr = str.toCharArray();
        int l = 0, r = arr.length - 1;
        while (l < r) {
            swap(arr, l, r);
            l++;
            r--;
        }
        return new String(arr);
    }

    private void swap(char[] arr, int i, int j) {
        char t = arr[i];
        arr[i] = arr[j];
        arr[j] = t;
    }

    public static void main(String[] args) {
        ShortestPalindrome palindrome = new ShortestPalindrome();
        System.out.println(palindrome.shortestPalindrome(""));
    }
}
